const servicesCard=document.querySelectorAll(".services__card"),modal=document.querySelector(".modal"),closeModalButton=document.querySelector(".modal__close");function showModal(e){e.preventDefault(),modal.classList.add("open");const o=e.currentTarget.querySelector(".services__card-banner").src,t=e.currentTarget.querySelector(".services__card-trainer").textContent,r=e.currentTarget.querySelector(".services__card-group-name").textContent;modal.querySelector(".modal__content-img").src=o,modal.querySelector(".heading__modal").textContent=r,modal.querySelector(".modal__info-trainer").textContent=t,document.body.style.overflow="hidden"}function closeModal(){modal.classList.remove("open"),modal.querySelector(".modal__content-img").src="",modal.querySelector(".heading__modal").textContent="",modal.querySelector(".modal__info-trainer").textContent="",document.body.style.overflow="auto"}closeModalButton.addEventListener("click",closeModal),servicesCard.forEach((e=>{e.addEventListener("click",showModal)}));